import { combineReducers, configureStore } from '@reduxjs/toolkit';
import { connectRouter, routerMiddleware } from 'connected-react-router';
import createSagaMiddleware from 'redux-saga';
import authReducer from '../features/auth/authSlice';
import categoryReducer from '../features/categories/categoriesSlice';
import createFixedCourseReducer from '../features/courses/create/fixedCourse/fixedCourseSlice';
import createFlexLessonReducer from '../features/courses/create/flexLesson/flexLessonSlice';
import privateCourseReducer from '../features/courses/private/fixedCourse/fixedCourseSlice';
import privateLessonReducer from '../features/courses/private/flexibleLesson/flexibleLessonSlice';
import publicCourseReducer from '../features/courses/public/fixedCourse/fixedCourseSlice';
import publicLessonReducer from '../features/courses/public/flexibleLesson/flexibleLessonSlice';
import googleMeetReducer from '../features/googleMeet/googleMeetSlice';
import modalReducer from '../features/modal/modal';
import dialogReducer from '../features/dialog/dialogSlice';
import promotionsReducer from '../features/promotions/promotionsSlice';
import settingReducer from '../features/settings/settingSlice';
import staffReducer from '../features/staffs/staffSlice';
import statisticReducer from '../features/statistic/statisticSlice';
import registerByReducer from '../features/studentRole/registerBy/registerBySlice';
import scheduleReducer from '../features/studentRole/schedule/scheduleSlice';
import settingStudentReducer from '../features/studentRole/settings/settingStudentSlice';
import studentsReducer from '../features/students/studentsSlice';
import scheduleTutorReducer from '../features/tutorRole/schedule/scheduleTutorSlice';
import settingTutorReducer from '../features/tutorRole/settings/settingTutorSile';
import tutorReducer from '../features/tutors/private/tutorsSlice';
import publicTutorReducer from '../features/tutors/public/tutorListSlice';
import paymentReducer from '../features/studentRole/payment/paymentSlice';
import { history } from '../utils/history';
import rootSaga from './rootSaga';

const rootReducer = combineReducers({
  router: connectRouter(history),
});

const sagaMiddleware = createSagaMiddleware();

const configStore = () => {
  const store = configureStore({
    reducer: rootReducer,
    middleware: (getDefaultMiddleware) =>
      getDefaultMiddleware({
        serializableCheck: false,
      }).concat(sagaMiddleware, routerMiddleware(history)),
  });
  sagaMiddleware.run(rootSaga);
  return store;
};
const store = configStore();

export default store;
